metadata = MapMetaData()
metadata.height = 47
metadata.width = 47
metadata.resolution = 0.05
metadata.origin.position.x = -1.175
metadata.origin.position.y = -1.175
#metadata.origin.orientation = Quaternion(*tf.transformations.quaternion_from_euler(0,0,math.radians(90)))

def pub_empty():
    map = OccupancyGrid()
    map.header.frame_id = "map"
    map.data = unlimited
    map.info = metadata
    pub.publish(map)
    #print("Map_limited: ", unlimited)

def pub_limits():
    map = OccupancyGrid()
    map.header.frame_id = "map"
    map.data = limited
    map.info = metadata
    pub.publish(map)
    #print("Map_unlimited: ", limited)

def srvCallback(data):
    response = SetBoolResponse()
    response.success = True
    if data.data:
        pub_limits()
        response.message = "limits activated!"
    else:
        pub_empty()
        response.message = "limits deactivated!"
    return response


if __name__ == '__main__':
    rospy.init_node('Arena_Limiter', anonymous=False)
    rospy.loginfo("node started")
    pub = rospy.Publisher('/custom_map', OccupancyGrid, latch=True, queue_size=5)
    metadata_pub = rospy.Publisher('/custom_map_metadata', MapMetaData, latch=True, queue_size=5)
    metadata_pub.publish(metadata)
    pub_empty()
    srv = rospy.Service("/arena_limits", SetBool, srvCallback)
    rospy.spin()